export const ORDERS = [];

export const seedDb = async () => {
  const adminUsersIdMap = {};
  const usersIdMap = {};
  const categoriesIdMap = {};
  const productsIdMap = {};
  const orderMap = {};

  let adminId = '';
  let userId = '';
  const productIds = [];

  for (const admin of ADMIN_USERS) {
    const object = await new userModel({
      name: admin.name,
      email: admin.email,
      password: await hashPassword(admin.password),
      phone: admin.phone,
      address: admin.address,
      answer: admin.answer,
      role: 1,
    }).save();
    adminUsersIdMap[admin.email] = object._id;
    adminId = object._id;
    console.log('Admin user created: ', admin.email);
  }
  console.log(adminUsersIdMap);

  //--

  for (const user of USERS) {
    const object = await new userModel({
      name: user.name,
      email: user.email,
      password: await hashPassword(user.password),
      phone: user.phone,
      address: user.address,
      answer: user.answer,
    }).save();
    usersIdMap[user.email] = object._id;
    userId = object._id;
    console.log("User created: ", user.email);
  }
  console.log(usersIdMap);

  //--

  for (const category of CATEGORIES) {
    const object = await new categoryModel({
      name: category,
      slug: slugify(category),
    }).save();
    categoriesIdMap[category] = object._id;
    console.log("Category created: ", category);
  }
  console.log(categoriesIdMap);

  //--

  for (const product of PRODUCTS) {
    const object = await new productModel({
      name: product.name,
      slug: slugify(product.name),
      description: product.description,
      price: product.price,
      category: categoriesIdMap[product.category],
      quantity: product.quantity,
      photo: {
        data: Buffer.from(product.photo.base64, "base64"),
        contentType: product.contentType,
      },
      shipping: product.shipping === "Yes",
    }).save();
    productsIdMap[product.name] = object._id;
    productIds.push(object._id);
    console.log("Product created: ", product.name);
  }
  console.log(productsIdMap);

  // --
  // create the order
  ORDERS.push({
    products: productIds,
    payment: {
      success: true,
    },
    buyer: userId,
    status: "Not Processed",
  });

  const order = await new orderModel({
    products: productIds,
    payment: {
      success: true,
    },
    buyer: userId,
    status: "Not Processed",
  }).save();
  orderMap[userId] = order._id;
  console.log("Order created: ", order);
};

